import React from "react";
import { useTranslation } from "react-i18next";
import "./transactionhistory.css";

export default function TransactionHistory({ events = [] }) {
  const { t } = useTranslation();

  return (
    <aside className="tx-history-box">
      <h3>{t("transaction_history")}</h3>
      {events.length === 0 ? (
        <p className="no-activity">{t("no_transactions")}</p>
      ) : (
        <ul className="tx-list">
          {events.map((e, i) => (
            <li key={i}>
              <strong>{t("transaction_id")}:</strong> {e.transactionHash.slice(0, 8)}â€¦
              <br />
              <strong>{t("tx_type")}:</strong> {e.event}
              <br />
              <strong>{t("block_number")}:</strong> {e.blockNumber}
              <br />
              <button onClick={() => console.log(e)}>{t("view_details")}</button>
            </li>
          ))}
        </ul>
      )}
    </aside>
  );
}
