import React from "react";
import { useTranslation } from "react-i18next";
import "./transactionhistory.css";

export default function TransactionHistory({ events = [] }) {
  const { t } = useTranslation();
  return (
    <aside className="tx-history-box">
      <h3>{t("transaction_history_header")}</h3>
      {events.length === 0 ? (
        <p className="no-activity">{t("no_recent_activity")}</p>
      ) : (
        <ul className="tx-list">
          {events.map((e, i) => (
            <li key={i}>
              <strong>{t(`event_${e.event}`)}</strong> —{" "}
              {e.transactionHash.slice(0, 8)}… (block {e.blockNumber})
            </li>
          ))}
        </ul>
      )}
    </aside>
  );
}
